options.customCss:
:root {
  /* Colors */
  --chat--color-primary: #FFD200; /* Mr. DIY Yellow */
  --chat--color-primary-gradient: linear-gradient(135deg, #FFD200 0%, #FFB300 100%);
  --chat--color-primary-shade-50: #e6bd00;
  --chat--color-primary-shade-100: #ccaa00;
  --chat--color-secondary: #C8102E; /* Mr. DIY Red */
  --chat--color-secondary-gradient: linear-gradient(135deg, #C8102E 0%, #F23048 100%);
  --chat--color-white: #ffffff;
  --chat--color-light: #fafafa;
  --chat--color-light-shade-50: #f0f0f0;
  --chat--color-light-shade-100: #dcdcdc;
  --chat--color-medium: #bfbfbf;
  --chat--color-dark: #333333;
  --chat--color-disabled: #999999;
  --chat--color-typing: #555555;

  /* Layout */
  --chat--spacing: 1rem;
  --chat--border-radius: 1rem;
  --chat--transition-duration: 0.3s;
  --chat--font-family: (
    'Roboto',
    'Open Sans',
    -apple-system,
    BlinkMacSystemFont,
    'Segoe UI',
    Oxygen-Sans,
    Ubuntu,
    Cantarell,
    'Helvetica Neue',
    sans-serif
  );

  /* Window */
  --chat--window--width: 400px;
  --chat--window--height: 600px;
  --chat--window--bottom: var(--chat--spacing);
  --chat--window--right: var(--chat--spacing);
  --chat--window--z-index: 9999;
  --chat--window--border: none;
  --chat--window--border-radius: 1.5rem;
  --chat--window--margin-bottom: var(--chat--spacing);
  --chat--window--box-shadow: 0 8px 30px rgba(0, 0, 0, 0.15);

  /* Header */
  --chat--header-height: auto;
  --chat--header--padding: var(--chat--spacing);
  --chat--header--background: var(--chat--color-secondary-gradient);
  --chat--header--color: var(--chat--color-white);
  --chat--heading--font-size: 1.6em;
  --chat--subtitle--font-size: 1em;
  --chat--subtitle--line-height: 1.6;

  /* Messages */
  --chat--message--font-size: 1rem;
  --chat--message--padding: var(--chat--spacing);
  --chat--message--border-radius: 1rem;
  --chat--message-line-height: 1.5;
  --chat--message--margin-bottom: 0.75rem;
  --chat--message--bot--background: var(--chat--color-white);
  --chat--message--bot--color: var(--chat--color-dark);
  --chat--message--user--background: var(--chat--color-primary-gradient);
  --chat--message--user--color: var(--chat--color-dark);
  --chat--message--pre--background: rgba(0, 0, 0, 0.05);
  --chat--messages-list--padding: var(--chat--spacing);
  --chat--message--shadow: 0 4px 10px rgba(0, 0, 0, 0.1);

  /* Toggle Button */
  --chat--toggle--size: 64px;
  --chat--toggle--border-radius: 50%;
  --chat--toggle--background: var(--chat--color-primary-gradient);
  --chat--toggle--hover--background: var(--chat--color-secondary-gradient);
  --chat--toggle--color: var(--chat--color-dark);
  --chat--toggle--box-shadow: 0 6px 15px rgba(0, 0, 0, 0.2);

  /* Input */
  --chat--textarea--height: 50px;
  --chat--textarea--max-height: 30rem;
  --chat--input--font-size: 1rem;
  --chat--input--border: 1px solid var(--chat--color-light-shade-100);
  --chat--input--border-radius: 0.75rem;
  --chat--input--padding: 0.8rem;
  --chat--input--background: var(--chat--color-white);
  --chat--input--text-color: var(--chat--color-dark);
  --chat--input--line-height: 1.5;
  --chat--input--border-active: 1px solid var(--chat--color-primary);
  --chat--input--box-shadow: 0 2px 8px rgba(0, 0, 0, 0.08);

  /* Buttons */
  --chat--button--color: var(--chat--color-dark);
  --chat--button--background: var(--chat--color-primary-gradient);
  --chat--button--padding: calc(var(--chat--spacing) * 0.5) var(--chat--spacing);
  --chat--button--border-radius: 2rem;
  --chat--button--hover--color: var(--chat--color-dark);
  --chat--button--hover--background: var(--chat--color-secondary-gradient);
  --chat--button--box-shadow: 0 4px 10px rgba(0, 0, 0, 0.1);

  /* Send and File Buttons */
  --chat--input--send--button--background: var(--chat--color-primary-gradient);
  --chat--input--send--button--color: var(--chat--color-dark);
  --chat--input--send--button--background-hover: var(--chat--color-secondary-gradient);
  --chat--input--send--button--color-hover: var(--chat--color-white);
  --chat--input--file--button--background: var(--chat--color-white);
  --chat--input--file--button--color: var(--chat--color-secondary);
  --chat--input--file--button--background-hover: var(--chat--color-light);
  --chat--input--file--button--color-hover: var(--chat--color-secondary-shade-50);
  --chat--files-spacing: 0.25rem;

  /* Body and Footer */
  --chat--body--background: var(--chat--color-light);
  --chat--footer--background: var(--chat--color-light);
  --chat--footer--color: var(--chat--color-dark);
}

/* Extra visual polish */
.chat-window {
  border-radius: var(--chat--window--border-radius);
  box-shadow: var(--chat--window--box-shadow);
  overflow: hidden;
}

.chat-header {
  background: var(--chat--header--background);
  color: var(--chat--header--color);
  font-family: 'Roboto', sans-serif;
  font-weight: 600;
  text-align: center;
  letter-spacing: 0.5px;
  box-shadow: 0 4px 10px rgba(200, 16, 46, 0.3);
}

.chat-message {
  max-width: 65%;
  padding: var(--chat--message--padding);
  border-radius: var(--chat--message--border-radius);
  box-shadow: var(--chat--message--shadow);
  font-family: 'Roboto', sans-serif;
  transition: transform 0.2s ease, box-shadow 0.2s ease;
}

.chat-message:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 15px rgba(0, 0, 0, 0.15);
}

.chat-toggle {
  background: var(--chat--toggle--background);
  box-shadow: var(--chat--toggle--box-shadow);
  transition: transform 0.3s ease;
}

.chat-toggle:hover {
  background: var(--chat--toggle--hover--background);
  transform: scale(1.1);
}

.chat-input {
  box-shadow: var(--chat--input--box-shadow);
  border-radius: var(--chat--input--border-radius);
}
